import React from 'react'


export default function Comments(props) {
    return (
        <>
            <div className="comment">
                <div className="comment__image"> </div>

                {/* comment subcontainer without user image */}
                <div className="comment__sub">
                    {/* name and date container */}
                    <div className="comment__sub-details">
                        <h4 className="comment__sub-name"> {props[0]["name"]} </h4> 
                        <h4 className="comment__sub-date"> {props["date"]} </h4> 
                    </div>
                {/* comment text */}
                <div className="comment__sub-text"> {props["comment"]} </div>
                </div>
            </div>  
        </>
    )
}
