  //       this.setState({
  //         sideVideos: responseArray[0].data,
  //         mainVideo: responseArray[1].data,
  //         loading: false
  //       });
  //     });
  // }

  // own api
  componentDidMount() {
    this.setState({ loading: true });
    axios.get("/api/videos").then(res => {
      this.setState({
        sideVideos: res.data,
        mainVideo: res.data,
        loading: false,
      });
    });
  }

  componentDidUpdate(prevProps) {
    console.log("Home test");
    console.log(this.props);
    console.log(prevProps);
    ///console.log(this.props.routerProps.location.pathname);
    const vidParams = this.props.match.params.id;
    const prevPropsParams = prevProps.match.params.id;
    // just props

    if (vidParams !== prevPropsParams) {
      console.log("vidParams", vidParams);
      // const vidId = this.props.match.params.id;
      this.fetchVideo(vidParams);
    }
    //  else if (this.props.routerProps.location.pathname === "/") {
