import React from 'react'
import poster from '../Assets/Images/video-list-0.jpg';
import video from '../Assets/Video/BrainStation Sample Video.mp4';

export default function Hero(props) {
    console.log("hero", props)

    if (props.currentVideo.image === undefined) {
        get.axios(
          "https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=59903f8f-ba1b-4870-afb2-527d1860f390"
        );
    }
      return (
        <div className="hero">
          <video
            className="hero__video"
            poster={props.currentVideo.image}
            src={video}
            controls
          />
        </div>
      );
}
