             <h4 className="video__data-title"> {vid.title}</h4>
             <h4 className="video__data-channel"> {vid.channel} </h4>
           </div>
         </div>
       </div>
      // {/* </Link> */}
      )})

      return 
        <>
          <p className="video__title">NEXT VIDEO</p>
          {mappedVideos}
        </>

    }
