    return arr.map(arr.props.name)
    return arr.map(arr.props.date);
    return arr.map(arr.props.comment);
}




import React, { Component } from 'react'

export default class Comments extends Component {

    render() {
        return (
            <>
             <div className="comment">
                 <div className="comment__image"> </div>

                 {/* comment subcontainer without user image */}
                 <div className="comment__sub">
                     {/* name and date container */}
                     <div className="comment__sub-details">
                         <h4 className="comment__sub-name"> {this.props.name} </h4> 
                         <h4 className="comment__sub-date"> {this.props.date} </h4> 
                     </div>
                 {/* comment text */}
                     <div className="comment__sub-text"> {this.props.comment} </div>
                 </div>
             </div>  
         </>
        )
    }
}
